import { Cart } from "./Cart";
import { ProductList } from "./ProductList";
import { useState, useEffect } from 'react'



function App() {

  const [products, setProducts] = useState([])
  const [cart, setCart] = useState([])
  useEffect(() => {
    fetch("https://fakestoreapi.com/products")
      .then(r => r.json())
      .then(r => setProducts(r))
  }, [])



  const moveToCart = prod => {
    let updatedCart;
    const found = cart.find(x => x.id === prod.id);

    if (!found) {
      updatedCart = [...cart, { id: prod.id, title: prod.title, category: prod.category, price: prod.price, count: 1 }];
    } else {
      updatedCart = cart.map(item => (item.id === prod.id ? { ...item, count: item.count + 1 } : item));
    }

    setCart(updatedCart);
  };
  const countUp = prod => {
    setCart([...cart], { ...prod.count++ })

  }

  return <div className="container">
    <div className="row">
      <ProductList
        data={products}
        onMove={moveToCart}


      />
      <Cart
        date={cart}
        onCount={countUp}
      />

    </div>


  </div>

}
export default App;
