// import React, { useState, useContext } from "react";
// import { BrowserRouter, Link, Route, Routes } from "react-router-dom";
// import { ProductList } from "./components/ProductList";
// import { Cart } from './components/Cart';
// import { ProductProvider } from "./Context";
// import { Add } from "./Add";
export const RoteConfig = () => {
    const { products, setsearchCategory } = useContext(ProductContext);
    
    const [searchCategory, setSearchCategory] = useState("");
    const [filteredProducts, setFilteredProducts] = useState([]); // Добавляем состояние для отфильтрованных товаров

    const handleSearch = () => {
        const lowerCaseSearchCategory = searchCategory.toLowerCase();
        const filtered = products.filter(elm => elm.category.toLowerCase().includes(lowerCaseSearchCategory));
        setFilteredProducts(filtered);
    };

    return (
        <BrowserRouter>
            <ProductProvider>
                <div className="header">
                    <div className="logo">Products</div>
                    <div className="navigation">
                        <div className="search">
                            <input
                                type="text"
                                placeholder="Search..."
                                className="search-input"
                                value={searchCategory}
                                onChange={(e) => setSearchCategory(e.target.value)}
                            />
                            <button className="search-button" onClick={handleSearch}>Search</button>
                        </div>
                        <Link to="/" className="nav-item">Home</Link>
                        <Link to="/basket" className="nav-item">Cart</Link>
                        <Link to="/Add" className="nav-item">Add</Link>
                    </div>
                </div>
                <Routes>
                    <Route path="Add" element={<Add />} />
                    <Route path="/" element={<ProductList products={filteredProducts.length ? filteredProducts : products} />} />
                    <Route path="/basket" element={<Cart />} />
                </Routes>
            </ProductProvider>
        </BrowserRouter>
    );
};
