function App() {

  const [products, setProducts] = useState([])
  const[cart,setCart]=useState([])
  useEffect(() => {
    fetch("https://fakestoreapi.com/products")
      .then(r => r.json())
      .then(r => setProducts(r))
  }, [])

  const Move = vue => {
    let found=cart.find(x=>x.id==vue.id)
    if(!found){
      setCart([...cart,{...products,count:1}])
    }else{
      found.count++
      setCart([...cart])
    }
  }






  return <div className="container">
    <h1>Shop</h1>
    <div className="row">
      <ProductList
        data={products}
        onMove={Move}

      />
      <Cart 
      date2={cart}
      />

    </div>


  </div>

}
export default App;
