import { useContext } from "react"
import { ProductContext } from "../Context"
import { useNavigate } from "react-router-dom"

export const ProductList = () => {
    const Navigate = useNavigate()
    const go = () => {
        Navigate("/basket");
    };
    

    const { products, moveToCart, isAddedToCart,} = useContext(ProductContext)
    return <div>
          {isAddedToCart && (
                <div className="alert alert-success" role="alert">
                    Product has been added to cart!
                </div>
            )}
        {
            <div className="grid-container">
                {
                    products.map(elm =>
                        <div key={elm.id} className="card">
                            {/* <img src={elm.image} style={{ width: 200, height: 200 }} /> */}
                            <p className="text-dark">{elm.category}</p>
                            <div className="card-content">
                            {/* <p >{elm.title}</p> */}
                            <p className="card-text">{elm.price} USD</p>
                            </div>
                            <button className="btn btn-danger" onClick={() => moveToCart(elm)}>Move</button>
                        </div>
                    )}
            </div>
        }

        <button className="btn btn-danger" onClick={go}>go</button>
    </div>
}
