import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { ProductContext } from "../Context";

export const ProductList = () => {
    const navigate = useNavigate();
    const { products, moveToCart } = useContext(ProductContext);
    const [searchTerm, setSearchTerm] = useState("");
    const [isSearchActive, setIsSearchActive] = useState(false);

    const filteredProducts = searchTerm 
        ? products.filter(product => product.category.toLowerCase().includes(searchTerm.toLowerCase()))
        : products;

    return (
        <div>
            <div className="search-icon" style={{ position: 'fixed', top: '10px', right: '10px', cursor: 'pointer' }}>
                <i className="fa fa-search" onClick={() => setIsSearchActive(!isSearchActive)}></i>
            </div>
            {isSearchActive && (
                <div style={{ position: 'fixed', top: '45px', right: '10px' }}>
                    <input
                        type="text"
                        placeholder="Search by category..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        autoFocus
                    />
                </div>
            )}

            <div className="grid-container">
                {filteredProducts.map(product => (
                    <div key={product.id} className="card">
                        <img src={product.image} alt={product.title} style={{ width: "200px", height: "200px" }} />
                        <p className="text-dark">{product.category}</p>
                        <div className="card-content">
                            <p>{product.title}</p>
                            <p className="card-text">{product.price} USD</p>
                            <button className="btn btn-danger" onClick={() => moveToCart(product)}>Move to Cart</button>
                        </div>
                    </div>
                ))}
            </div>
            <button className="btn btn-danger" onClick={() => navigate('/cart')}>Go to Cart</button>
        </div>
    );
};
