import React, { useState } from "react";

export const RoteConfig = () => {
    const [searchTerm, setSearchTerm] = useState("");

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
    };

    return (
        <BrowserRouter>
            <ProductProvaider>
                <div className="header">
                    <div className="logo">Products</div>
                    <div className="search-container">
                        <input
                            type="text"
                            placeholder="Search by category"
                            value={searchTerm}
                            onChange={handleSearchChange}
                            className="search-input"
                        />
                        {/* Добавьте логику для фильтрации товаров */}
                    </div>
                    <div className="navigation">
                        <Link to="/" className="nav-item">Home</Link>
                        <Link to="/basket" className="nav-item">Cart</Link>
                        <Link to="/Add" className="nav-item">Add</Link>
                    </div>
                </div>
                <Routes>
                    <Route path="Add" element={<Add />} />
                    <Route path="/" element={<ProductList searchTerm={searchTerm} />} />
                    <Route path="/basket" element={<Cart />} />
                </Routes>
            </ProductProvaider>
        </BrowserRouter>
    );
};
