import { ProductList } from "./ProductList";
import { ProductItem } from "./ProductItem";
import { CartItem } from "./CartItem";
import { Cart } from "./Cart";
import { useState, useEffect } from 'react';

function App() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    fetch("https://fakestoreapi.com/products")
      .then(r => r.json())
      .then(r => setProducts(r))
  }, []);

  const moveToCart = id => {
    let found = cart.find(x => x.id === id.id);
    if (!found) {
      setCart([...cart, { ...id, count: 1 }]);
    } else {
      found.count++;
      setCart([...cart]);
    }
  }

  const countUp = prod => {
    setCart([...cart], { ...prod.count++ });
  }

  const countDown = prod => {
    if (prod.count > 0) {
      prod.count--;
      setCart([...cart]);
    }
  }

  const Delete = prod => {
    setCart(cart.filter(a => a !== prod));
  }

  return (
    <div className="container">
      <div className="row">
        <ProductList
          date={products}
          onMove={moveToCart}
        />
        <Cart
          datee={cart}
          onCountUp={countUp}
          onCountDown={countDown}
          onDelete={Delete}
        />
        <ProductItem
          date={products}

        />

        {/* <CartItem
          date={cart}
          onCountUp={countUp}
          onCountDown={countDown}
          onDelete={Delete}
        /> */}


      </div>
    </div>
  );
}

export default App;
