import './App.css';
import { useEffect, useState } from 'react';

function App() {
  const [people, setPeople] = useState([]);
useEffect(()=>{
  fetch("https://randomuser.me/api?results=20")
  .then((res)=>res.json())
  .then()
})

const Delate =man=>{
  setPeople(people.filter(a=>a!==man))
}





 return<div>
{
people.map(elm=>{
  return <div key={elm.phone}>
    <h3>{elm.name.first}
     {elm.name.last}
      <img src={elm.picture.large}/> {elm.registered.age} <button onClick={()=>Delate(elm)}>Delate</button></h3>
    </div>
})
}

 </div>
}
export default App;
















