import { useContext } from "react";
import { ProductContext } from "../Context";

export const ProductList = () => {
    const { products, moveToCart, isAddedToCart } = useContext(ProductContext);

    return (
        <div>
            {isAddedToCart && (
                <div className="alert alert-success" role="alert">
                    Product has been added to cart!
                </div>
            )}
            <div className="grid-container">
                {products.map((product) => (
                    <div key={product.id} className="card">
                        <img src={product.image} style={{ width: 200, height: 200 }} alt={product.title} />
                        <p className="text-dark">{product.category}</p>
                        <div className="card-content">
                            <p>{product.title}</p>
                            <p className="card-text">{product.price} USD</p>
                        </div>
                        <button className="btn btn-danger" onClick={() => moveToCart(product)}>Move</button>
                    </div>
                ))}
            </div>
        </div>
    );
};
