import React, { useContext } from "react";
import { ProductContext } from "../Context";

export const ProductList = () => {
    const { products, moveToCart, isAddedToCart } = useContext(ProductContext)
    const [searchQuery, setSearchQuery] = useState(""); // State to hold the search query
       // Filter products based on search query
//     const filteredProducts = products.filter(product =>
//         product.category.toLowerCase().includes(searchQuery.toLowerCase())
//     );


    return (
        <div>
            <input
                type="text"
                placeholder="Search by category..."
                value={searchQuery}
                onChange={e => setSearchQuery(e.target.value)}
                className="form-control my-3"
            />
            {isAddedToCart && (
                <div className="alert">Product has been added to cart!</div>
            )}
            <div className="grid-container">
                {products.map(elm => (
                    <div key={elm.id} className="card">
                        <img src={elm.image} alt="Product" className="product-image" />
                        <p className="product-category">{elm.category}</p>
                        <div className="card-content">
                            <p>{elm.title}</p>
                            <p className="product-price">{elm.price} USD</p>
                        </div>
                        <button className="add-to-cart-btn" onClick={() => moveToCart(elm)}>ADD TO CART</button>
                    </div>
                ))}
            </div>
        </div>
    )
}


