// components/Cart.js
import React, { useContext } from "react";
import { ProductContext } from "../Context";

export const Cart = () => {
    const { cart, total, countUp, countDown, Delete } = useContext(ProductContext)

    return (
        <div>
            <h3 className="text-danger">Cart {total}</h3>
            <table className="cart-table">
                <thead>
                    <tr>
                        <th>Id</th>
                        <th>Category</th>
                        <th>Price</th>
                        <th>Quantity</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {cart.map(elm => (
                        <tr key={elm.id}>
                            <td>{elm.id}</td>
                            <td>{elm.category}</td>
                            <td> {elm.price} $</td>
                            <td>{elm.count}</td>
                            <td>
                                <button className="action-btn" onClick={() => countUp(elm)}>+</button>
                                <button className="action-btn" onClick={() => countDown(elm)}>-</button>
                                <button className="action-btn" onClick={() => Delete(elm)}>x</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};
