function App() {

  const [products, setProducts] = useState([])
  const [cart, setCart] = useState([])
  const [total, SetTotal] = useState(0)
  useEffect(() => {

    fetch("https://fakestoreapi.com/products")
      .then(r => r.json())
      .then(r => setProducts(r))

  }, [])
  const moveToCart = id => {
    let found = cart.find(x => x.id == id.id)
    if (!found) {
      setCart([...cart, { ...id, count: 1 }])
    } else {
      found.count++
      setCart([...cart])
    }
  }
  const countUp = prod => {
    setCart([...cart], { ...prod.count++ })

  }

  const countDown = prod => {
    setCart([...cart], { ...prod })
    if (prod.count > 0) {
      prod.count--
      if (prod.count < 0) {
        prod.count = 0
      }
    }
  }

  const Delete = prod => {
    setCart(cart.filter(a => a !== prod))
  }


  return <div className="container">
    <div className="row">
      <ProductList
        data={products}
        onMove={moveToCart}
      />
      <Cart
        date={cart}
        onCount={countUp}
        onCount2={countDown}
        onDelete={Delete}
      />

    </div>


  </div>

}
